// RegularMatch 正则匹配
func RegularMatch(instanceName string) bool {
	return gregex.IsMatchString("^[a-zA-Z0-9\u4e00-\u9fa5_-]{1,60}$", instanceName)
}

func DealParamStrValue(val string) string {
	target := val
	if strings.Contains(val, "'") {
		target = strings.Split(val, "'")[1]
	}
	log.Println(target)
	return target
}

